package org.catinthedark.jvcrplotter.game

private fun toMatrix(l: List<Int>, w: Int, h: Int): List<List<Int>> {
    val m = List(w) { MutableList(h) { 0 } }
    for (i in 0..w) {
        for (j in 0..h) {
            m[i][j] = l[i + j * w]
        }
    }

    return m
}

private fun toSeq(line: String) = line.split(",").map { it.toInt() }

